import React from 'react';
import { X, ExternalLink, Download, Calendar, MapPin, Building, DollarSign, FileText } from 'lucide-react';
import { Button } from './ui/Button';

export default function LicitacionModal({ licitacion, isOpen, onClose }) {
  if (!isOpen || !licitacion) return null;

  const formatCurrency = (amount) => {
    if (!amount) return 'No especificado';
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No especificada';
    return new Date(dateString).toLocaleDateString('es-CL', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-2xl font-bold text-gray-900">
            Detalles de Licitación
          </h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Información principal */}
          <div>
            <h3 className="text-xl font-semibold text-gray-900 mb-4">
              {licitacion.nombre || licitacion.titulo}
            </h3>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-3">
                <div className="flex items-center space-x-2">
                  <Building className="h-5 w-5 text-blue-600" />
                  <span className="font-medium">Responsable:</span>
                  <span className="text-gray-700">{licitacion.responsable || 'No especificado'}</span>
                </div>
                
                <div className="flex items-center space-x-2">
                  <MapPin className="h-5 w-5 text-green-600" />
                  <span className="font-medium">Región:</span>
                  <span className="text-gray-700">{licitacion.region || 'No especificada'}</span>
                </div>
                
                <div className="flex items-center space-x-2">
                  <DollarSign className="h-5 w-5 text-yellow-600" />
                  <span className="font-medium">Monto:</span>
                  <span className="text-gray-700 font-semibold">
                    {formatCurrency(licitacion.Monto || licitacion.monto)}
                  </span>
                </div>
              </div>
              
              <div className="space-y-3">
                <div className="flex items-center space-x-2">
                  <Calendar className="h-5 w-5 text-purple-600" />
                  <span className="font-medium">Fecha Publicación:</span>
                  <span className="text-gray-700">
                    {formatDate(licitacion.fechaPublicacion || licitacion.fecha_publicacion)}
                  </span>
                </div>
                
                <div className="flex items-center space-x-2">
                  <FileText className="h-5 w-5 text-red-600" />
                  <span className="font-medium">Estado:</span>
                  <span className={`px-2 py-1 rounded-full text-xs font-semibold ${
                    licitacion.estado?.toLowerCase().includes('abierta') 
                      ? 'bg-green-100 text-green-800'
                      : licitacion.estado?.toLowerCase().includes('cerrada')
                      ? 'bg-red-100 text-red-800'
                      : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {licitacion.estado || 'No especificado'}
                  </span>
                </div>
                
                <div className="flex items-center space-x-2">
                  <FileText className="h-5 w-5 text-gray-600" />
                  <span className="font-medium">ID:</span>
                  <span className="text-gray-700 font-mono">{licitacion.id}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Descripción */}
          {licitacion.descripcion && (
            <div>
              <h4 className="text-lg font-semibold text-gray-900 mb-2">Descripción</h4>
              <p className="text-gray-700 leading-relaxed">
                {licitacion.descripcion}
              </p>
            </div>
          )}

          {/* Información adicional */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="text-lg font-semibold text-gray-900 mb-3">Información Adicional</h4>
              <div className="space-y-2 text-sm">
                {licitacion.categoria && (
                  <div className="flex justify-between">
                    <span className="font-medium">Categoría:</span>
                    <span className="text-gray-700">{licitacion.categoria}</span>
                  </div>
                )}
                {licitacion.fechaCierre && (
                  <div className="flex justify-between">
                    <span className="font-medium">Fecha Cierre:</span>
                    <span className="text-gray-700">
                      {formatDate(licitacion.fechaCierre || licitacion.fecha_cierre)}
                    </span>
                  </div>
                )}
                {licitacion.organismo && (
                  <div className="flex justify-between">
                    <span className="font-medium">Organismo:</span>
                    <span className="text-gray-700">{licitacion.organismo}</span>
                  </div>
                )}
                {licitacion.tipoCompra && (
                  <div className="flex justify-between">
                    <span className="font-medium">Tipo de Compra:</span>
                    <span className="text-gray-700">{licitacion.tipoCompra}</span>
                  </div>
                )}
              </div>
            </div>

            <div>
              <h4 className="text-lg font-semibold text-gray-900 mb-3">Enlaces</h4>
              <div className="space-y-3">
                {licitacion.urlLicitacion && (
                  <a
                    href={licitacion.urlLicitacion}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center space-x-2 text-blue-600 hover:text-blue-800 transition-colors"
                  >
                    <ExternalLink className="h-4 w-4" />
                    <span>Ver Licitación Original</span>
                  </a>
                )}
                
                {licitacion.urlAdjuntos && (
                  <a
                    href={licitacion.urlAdjuntos}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center space-x-2 text-green-600 hover:text-green-800 transition-colors"
                  >
                    <Download className="h-4 w-4" />
                    <span>Ver Adjuntos</span>
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="flex justify-end p-6 border-t border-gray-200 space-x-3">
          <Button
            variant="outline"
            onClick={onClose}
          >
            Cerrar
          </Button>
          
          {licitacion.urlLicitacion && (
            <Button
              onClick={() => window.open(licitacion.urlLicitacion, '_blank')}
            >
              <ExternalLink className="h-4 w-4 mr-2" />
              Ver Licitación
            </Button>
          )}
        </div>
      </div>
    </div>
  );
}
