import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  Search, Download, ChevronLeft, ChevronRight,
  Eye, Link as LinkIcon, Paperclip
} from 'lucide-react';
import { CSVLink } from 'react-csv';
import * as XLSX from 'xlsx';
import { apiService } from '../services/api';
import LicitacionModal from './LicitacionModal';

export default function DashboardPage() {
  const { region: regionParam } = useParams();
  const navigate = useNavigate();
  
  const [licitaciones, setLicitaciones] = useState([]);
  const [estadisticas, setEstadisticas] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Modal state
  const [selectedLicitacion, setSelectedLicitacion] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  
  // Filtros
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRegion, setSelectedRegion] = useState(regionParam ? decodeURIComponent(regionParam) : '');
  const [selectedEstado, setSelectedEstado] = useState('');
  const [fechaDesde, setFechaDesde] = useState('');
  const [fechaHasta, setFechaHasta] = useState('');
  
  // Paginación
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [totalPages, setTotalPages] = useState(1);
  const [totalItems, setTotalItems] = useState(0);
  
  // Ordenamiento
  const [sortField, setSortField] = useState('fechaPublicacion');
  const [sortDirection, setSortDirection] = useState('desc');

  // Cargar datos iniciales
  const loadData = useCallback(async () => {
    setLoading(true);
    setError(null);
    
    try {
      const params = {
        page: currentPage,
        limit: itemsPerPage,
        region: selectedRegion || undefined,
        estado: selectedEstado || undefined,
        fechaInicio: fechaDesde || undefined,
        fechaFin: fechaHasta || undefined,
      };

      const response = await apiService.getLicitaciones(params);
      
      if (response.success) {
        setLicitaciones(response.data);
        setEstadisticas(response.estadisticas);
        setTotalPages(response.pagination.pages);
        setTotalItems(response.pagination.total);
      } else {
        setError('Error al cargar los datos');
      }
    } catch (err) {
      setError(err.message);
      console.error('Error loading data:', err);
    } finally {
      setLoading(false);
    }
  }, [currentPage, selectedRegion, selectedEstado, fechaDesde, fechaHasta, itemsPerPage]);

  useEffect(() => {
    loadData();
  }, [loadData]);

  // Filtrar licitaciones localmente por búsqueda
  const filteredLicitaciones = useMemo(() => {
    if (!searchTerm) return licitaciones;
    
    return licitaciones.filter(lic => {
      const searchLower = searchTerm.toLowerCase();
      return (
        (lic.nombre && lic.nombre.toLowerCase().includes(searchLower)) ||
        (lic.responsable && lic.responsable.toLowerCase().includes(searchLower)) ||
        (lic.region && lic.region.toLowerCase().includes(searchLower)) ||
        (lic.estado && lic.estado.toLowerCase().includes(searchLower))
      );
    });
  }, [licitaciones, searchTerm]);

  const formatCurrency = (amount) => {
    if (!amount) return 'No especificado';
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No especificada';
    return new Date(dateString).toLocaleDateString('es-CL');
  };

  const clearFilters = () => {
    setSearchTerm('');
    setSelectedRegion('');
    setSelectedEstado('');
    setFechaDesde('');
    setFechaHasta('');
    setCurrentPage(1);
  };

  const openModal = async (licitacion) => {
    try {
      const response = await apiService.getLicitacionDetail(licitacion.id);
      if (response.success) {
        setSelectedLicitacion(response.data);
        setIsModalOpen(true);
      }
    } catch (err) {
      console.error('Error loading licitacion details:', err);
    }
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredLicitaciones);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Licitaciones");
    XLSX.writeFile(wb, "licitaciones.xlsx");
  };

  // Datos para CSV
  const csvData = filteredLicitaciones.map(lic => ({
    ID: lic.id,
    Nombre: lic.nombre,
    Responsable: lic.responsable,
    Monto: lic.Monto,
    Región: lic.region,
    Estado: lic.estado,
    'Fecha Publicación': lic.fechaPublicacion,
    'URL Licitación': lic.urlLicitacion,
    'URL Adjuntos': lic.urlAdjuntos
  }));

  if (loading && licitaciones.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-red-600 mb-4">Error</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={loadData}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
          >
            Reintentar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">
                Dashboard de Licitaciones
              </h1>
              {selectedRegion && (
                <p className="text-gray-600 mt-1">
                  Mostrando resultados para: <span className="font-semibold">{selectedRegion}</span>
                </p>
              )}
            </div>
            
            {/* Fecha de extracción */}
            {estadisticas.fechaUltimaActualizacion && (
              <div className="text-right">
                <p className="text-sm text-gray-500">Última actualización:</p>
                <p className="text-sm font-medium text-gray-900">
                  {formatDate(estadisticas.fechaUltimaActualizacion)}
                </p>
              </div>
            )}
            
            {/* Botones de descarga */}
            <div className="flex space-x-2">
              <CSVLink
                data={csvData}
                filename="licitaciones.csv"
                className="inline-flex items-center space-x-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors"
              >
                <Download className="h-4 w-4" />
                <span>CSV</span>
              </CSVLink>
              
              <button
                onClick={exportToExcel}
                className="inline-flex items-center space-x-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                <Download className="h-4 w-4" />
                <span>Excel</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Panel de filtros */}
          <div className="lg:w-80">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-lg font-semibold text-gray-900">Filtros</h2>
                <button
                  onClick={clearFilters}
                  className="text-sm text-blue-600 hover:text-blue-800"
                >
                  Limpiar filtros
                </button>
              </div>

              <div className="space-y-6">
                {/* Búsqueda */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Búsqueda
                  </label>
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                    <input
                      type="text"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      placeholder="Buscar licitaciones..."
                      className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>
                </div>

                {/* Región */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Región
                  </label>
                  <select
                    value={selectedRegion}
                    onChange={(e) => setSelectedRegion(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">Todas las regiones</option>
                    {Object.keys(estadisticas.porRegion || {}).map(region => (
                      <option key={region} value={region}>
                        {region} ({estadisticas.porRegion[region]})
                      </option>
                    ))}
                  </select>
                </div>

                {/* Estado */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Estado
                  </label>
                  <select
                    value={selectedEstado}
                    onChange={(e) => setSelectedEstado(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">Todos los estados</option>
                    {Object.keys(estadisticas.porEstado || {}).map(estado => (
                      <option key={estado} value={estado}>
                        {estado} ({estadisticas.porEstado[estado]})
                      </option>
                    ))}
                  </select>
                </div>

                {/* Rango de fechas */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Fechas
                  </label>
                  <div className="space-y-2">
                    <input
                      type="date"
                      value={fechaDesde}
                      onChange={(e) => setFechaDesde(e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                    <input
                      type="date"
                      value={fechaHasta}
                      onChange={(e) => setFechaHasta(e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Contenido principal */}
          <div className="flex-1">
            {/* Estadísticas de resultados */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-6">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">
                    {totalItems} licitaciones encontradas
                  </h3>
                  <p className="text-gray-600">
                    Página {currentPage} de {totalPages}
                  </p>
                </div>
                
                <div className="flex items-center space-x-4">
                  <span className="text-sm text-gray-600">Ordenar por:</span>
                  <select
                    value={sortField}
                    onChange={(e) => setSortField(e.target.value)}
                    className="px-3 py-1 border border-gray-300 rounded-lg text-sm"
                  >
                    <option value="fechaPublicacion">Fecha Publicación</option>
                    <option value="nombre">Nombre</option>
                    <option value="Monto">Monto</option>
                    <option value="region">Región</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Tabla de licitaciones */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Licitación
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Responsable
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Monto
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Región
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Estado
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Fecha Publicación
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Acciones
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredLicitaciones.map((licitacion) => (
                      <tr key={licitacion.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4">
                          <div>
                            <h4 className="text-sm font-medium text-gray-900 mb-1">
                              {licitacion.nombre}
                            </h4>
                            <p className="text-xs text-gray-500">
                              ID: {licitacion.id}
                            </p>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="text-sm text-gray-900">{licitacion.responsable}</span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="text-sm font-semibold text-gray-900">
                            {formatCurrency(licitacion.Monto)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="text-sm text-gray-900">{licitacion.region}</span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                            licitacion.estado?.toLowerCase().includes('abierta') 
                              ? 'bg-green-100 text-green-800'
                              : licitacion.estado?.toLowerCase().includes('cerrada')
                              ? 'bg-red-100 text-red-800'
                              : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {licitacion.estado}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatDate(licitacion.fechaPublicacion)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <div className="flex space-x-2">
                            <button
                              onClick={() => openModal(licitacion)}
                              className="inline-flex items-center space-x-1 text-blue-600 hover:text-blue-800"
                              title="Ver más datos"
                            >
                              <Eye className="h-4 w-4" />
                            </button>
                            
                            {licitacion.urlLicitacion && (
                              <a
                                href={licitacion.urlLicitacion}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="inline-flex items-center space-x-1 text-green-600 hover:text-green-800"
                                title="Ver licitación"
                              >
                                <LinkIcon className="h-4 w-4" />
                              </a>
                            )}
                            
                            {licitacion.urlAdjuntos && (
                              <a
                                href={licitacion.urlAdjuntos}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="inline-flex items-center space-x-1 text-purple-600 hover:text-purple-800"
                                title="Ver adjuntos"
                              >
                                <Paperclip className="h-4 w-4" />
                              </a>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Paginación */}
              {totalPages > 1 && (
                <div className="bg-white px-4 py-3 border-t border-gray-200 sm:px-6">
                  <div className="flex items-center justify-between">
                    <div className="flex-1 flex justify-between sm:hidden">
                      <button
                        onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                        disabled={currentPage === 1}
                        className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50"
                      >
                        Anterior
                      </button>
                      <button
                        onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                        disabled={currentPage === totalPages}
                        className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50"
                      >
                        Siguiente
                      </button>
                    </div>
                    <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
                      <div>
                        <p className="text-sm text-gray-700">
                          Mostrando <span className="font-medium">{(currentPage - 1) * itemsPerPage + 1}</span> a{' '}
                          <span className="font-medium">
                            {Math.min(currentPage * itemsPerPage, totalItems)}
                          </span> de{' '}
                          <span className="font-medium">{totalItems}</span> resultados
                        </p>
                      </div>
                      <div>
                        <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                          <button
                            onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                            disabled={currentPage === 1}
                            className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50"
                          >
                            <ChevronLeft className="h-5 w-5" />
                          </button>
                          
                          {/* Números de página */}
                          {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                            const page = Math.max(1, Math.min(totalPages - 4, currentPage - 2)) + i;
                            return (
                              <button
                                key={page}
                                onClick={() => setCurrentPage(page)}
                                className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                                  currentPage === page
                                    ? 'z-10 bg-blue-50 border-blue-500 text-blue-600'
                                    : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                                }`}
                              >
                                {page}
                              </button>
                            );
                          })}
                          
                          <button
                            onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                            disabled={currentPage === totalPages}
                            className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50"
                          >
                            <ChevronRight className="h-5 w-5" />
                          </button>
                        </nav>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Modal de detalles */}
      <LicitacionModal
        licitacion={selectedLicitacion}
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedLicitacion(null);
        }}
      />
    </div>
  );
}
