import React from 'react';
import { X, ExternalLink, Paperclip, Calendar, MapPin, Building, DollarSign, Clock } from 'lucide-react';

export default function LicitacionModal({ licitacion, isOpen, onClose }) {
  if (!isOpen || !licitacion) return null;

  const formatCurrency = (amount) => {
    if (!amount) return 'No especificado';
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No especificada';
    return new Date(dateString).toLocaleDateString('es-CL');
  };

  const getDaysUntilClose = (fechaCierre) => {
    if (!fechaCierre) return null;
    const today = new Date();
    const closeDate = new Date(fechaCierre);
    const diffTime = closeDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  const daysUntilClose = getDaysUntilClose(licitacion.fechaCierre || licitacion.fecha_cierre);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-6xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Detalles de Licitación</h2>
            <p className="text-sm text-gray-600 mt-1">ID: {licitacion.id}</p>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Información principal */}
            <div className="lg:col-span-2 space-y-6">
              {/* Título y descripción */}
              <div className="bg-blue-50 p-4 rounded-lg">
                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                  {licitacion.titulo || licitacion.nombre}
                </h3>
                {licitacion.descripcion && (
                  <p className="text-gray-700 leading-relaxed">
                    {licitacion.descripcion}
                  </p>
                )}
              </div>

              {/* Información detallada */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-4">
                  <div className="flex items-start space-x-3">
                    <Building className="h-5 w-5 text-blue-600 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">Responsable / Empresa Solicitante</p>
                      <p className="text-sm text-gray-600">{licitacion.responsable || licitacion.empresa_solicitante || 'No especificado'}</p>
                    </div>
                  </div>

                  <div className="flex items-start space-x-3">
                    <MapPin className="h-5 w-5 text-green-600 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">Región</p>
                      <p className="text-sm text-gray-600">{licitacion.region}</p>
                    </div>
                  </div>

                  <div className="flex items-start space-x-3">
                    <DollarSign className="h-5 w-5 text-yellow-600 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">Monto</p>
                      <p className="text-sm text-gray-600 font-semibold">
                        {formatCurrency(licitacion.Monto !== undefined ? licitacion.Monto : licitacion.monto)}
                      </p>
                    </div>
                  </div>
                </div>

                <div className="space-y-4">
                  <div className="flex items-start space-x-3">
                    <Calendar className="h-5 w-5 text-blue-600 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">Fecha de Publicación</p>
                      <p className="text-sm text-gray-600">
                        {formatDate(licitacion.fechaPublicacion || licitacion.fecha_publicacion)}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-start space-x-3">
                    <Clock className="h-5 w-5 text-red-600 mt-0.5" />
                    <div>
                      <p className="text-sm font-medium text-gray-900">Fecha de Cierre</p>
                      <p className="text-sm text-gray-600">
                        {formatDate(licitacion.fechaCierre || licitacion.fecha_cierre)}
                      </p>
                      {daysUntilClose !== null && (
                        <p className={`text-xs mt-1 ${
                          daysUntilClose < 0 
                            ? 'text-red-600 font-semibold' 
                            : daysUntilClose <= 7 
                            ? 'text-orange-600 font-semibold' 
                            : 'text-green-600'
                        }`}>
                          {daysUntilClose < 0 
                            ? `Cerrada hace ${Math.abs(daysUntilClose)} días` 
                            : daysUntilClose === 0 
                            ? 'Cierra hoy' 
                            : `${daysUntilClose} días restantes`
                          }
                        </p>
                      )}
                    </div>
                  </div>



                </div>
              </div>

              {/* Estado con más detalles */}
              <div className="bg-gray-50 p-4 rounded-lg">
                <p className="text-sm font-medium text-gray-900 mb-2">Estado de la Licitación</p>
                <div className="flex items-center space-x-3">
                  <span className={`inline-flex px-3 py-1 text-sm font-semibold rounded-full ${
                    licitacion.estado?.toLowerCase().includes('abierta') 
                      ? 'bg-green-100 text-green-800'
                      : licitacion.estado?.toLowerCase().includes('cerrada')
                      ? 'bg-red-100 text-red-800'
                      : licitacion.estado?.toLowerCase().includes('adjudicada')
                      ? 'bg-blue-100 text-blue-800'
                      : licitacion.estado?.toLowerCase().includes('desierta')
                      ? 'bg-gray-100 text-gray-800'
                      : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {licitacion.estado}
                  </span>
                  {licitacion.estado?.toLowerCase().includes('abierta') && daysUntilClose !== null && daysUntilClose > 0 && (
                    <span className="text-sm text-gray-600">
                      • Abierta para postulaciones
                    </span>
                  )}
                </div>
              </div>

              {/* Información adicional */}
              {(licitacion.organismo || licitacion.tipoCompra || licitacion.empresa_solicitante) && (
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <h4 className="text-lg font-semibold text-gray-900 mb-4">Información Adicional</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                    {licitacion.organismo && (
                      <div className="flex justify-between">
                        <span className="text-gray-600">Organismo:</span>
                        <span className="font-medium">{licitacion.organismo}</span>
                      </div>
                    )}
                    {licitacion.tipoCompra && (
                      <div className="flex justify-between">
                        <span className="text-gray-600">Tipo de Compra:</span>
                        <span className="font-medium">{licitacion.tipoCompra}</span>
                      </div>
                    )}
                    {licitacion.empresa_solicitante && (
                      <div className="flex justify-between">
                        <span className="text-gray-600">Empresa Solicitante:</span>
                        <span className="font-medium">{licitacion.empresa_solicitante}</span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Información adicional específica */}
              <div className="bg-white border border-gray-200 rounded-lg p-4 mt-6">
                <h4 className="text-lg font-semibold text-gray-900 mb-4">Datos adicionales</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                  {licitacion.fechaInicioPreguntas && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Inicio Preguntas:</span>
                      <span className="font-medium">{licitacion.fechaInicioPreguntas}</span>
                    </div>
                  )}
                  {licitacion.fechaFinPreguntas && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Fin Preguntas:</span>
                      <span className="font-medium">{licitacion.fechaFinPreguntas}</span>
                    </div>
                  )}
                  {licitacion.fechaPublicacionRespuestas && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Publicación Respuestas:</span>
                      <span className="font-medium">{licitacion.fechaPublicacionRespuestas}</span>
                    </div>
                  )}
                  {licitacion.fechaAdjudicacion && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Adjudicación:</span>
                      <span className="font-medium">{licitacion.fechaAdjudicacion}</span>
                    </div>
                  )}
                  {licitacion.fechaVisitaTerreno && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Visita a Terreno:</span>
                      <span className="font-medium">{licitacion.fechaVisitaTerreno}</span>
                    </div>
                  )}
                  {licitacion.fechaExtracción && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Fecha Extracción:</span>
                      <span className="font-medium">{licitacion.fechaExtracción}</span>
                    </div>
                  )}
                  {licitacion.Monto === undefined && licitacion.monto && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Monto:</span>
                      <span className="font-medium">{licitacion.monto}</span>
                    </div>
                  )}
                </div>
              </div>

              {/* Criterios de Evaluación */}
              {licitacion.criteriosEvaluacion && Array.isArray(licitacion.criteriosEvaluacion) && licitacion.criteriosEvaluacion.length > 0 && (
                <div className="bg-white border border-gray-200 rounded-lg p-4 mt-6">
                  <h4 className="text-lg font-semibold text-gray-900 mb-4">Criterios de Evaluación</h4>
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Criterio</th>
                          <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ponderación</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {licitacion.criteriosEvaluacion.map((criterio, idx) => (
                          <tr key={idx}>
                            <td className="px-4 py-2 text-sm text-gray-900">{criterio.item}</td>
                            <td className="px-4 py-2 text-sm text-gray-900">{criterio.ponderacion}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}
            </div>

            {/* Panel lateral con enlaces y acciones */}
            <div className="space-y-6">
              {/* Enlaces */}
              <div className="bg-white border border-gray-200 rounded-lg p-4">
                <h4 className="text-lg font-semibold text-gray-900 mb-4">Enlaces y Documentos</h4>
                <div className="space-y-3">
                  {licitacion.urlLicitacion && (
                    <a
                      href={licitacion.urlLicitacion}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center space-x-2 text-blue-600 hover:text-blue-800 p-3 border border-blue-200 rounded-lg hover:bg-blue-50 transition-colors"
                    >
                      <ExternalLink className="h-5 w-5" />
                      <span>Ver Licitación Original</span>
                    </a>
                  )}

                  {licitacion.urlAdjuntos && (
                    <a
                      href={licitacion.urlAdjuntos}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center space-x-2 text-purple-600 hover:text-purple-800 p-3 border border-purple-200 rounded-lg hover:bg-purple-50 transition-colors"
                    >
                      <Paperclip className="h-5 w-5" />
                      <span>Ver Adjuntos</span>
                    </a>
                  )}

                  {licitacion.url_original && (
                    <a
                      href={licitacion.url_original}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center space-x-2 text-green-600 hover:text-green-800 p-3 border border-green-200 rounded-lg hover:bg-green-50 transition-colors"
                    >
                      <ExternalLink className="h-5 w-5" />
                      <span>Mercado Público</span>
                    </a>
                  )}

                  {licitacion.link && (
                    <a
                      href={licitacion.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center space-x-2 text-indigo-600 hover:text-indigo-800 p-3 border border-indigo-200 rounded-lg hover:bg-indigo-50 transition-colors"
                    >
                      <ExternalLink className="h-5 w-5" />
                      <span>Enlace Externo</span>
                    </a>
                  )}
                </div>
              </div>

              {/* Resumen de fechas */}
              <div className="bg-white border border-gray-200 rounded-lg p-4">
                <h4 className="text-lg font-semibold text-gray-900 mb-4">Cronograma</h4>
                <div className="space-y-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Publicación:</span>
                    <span className="font-medium">{formatDate(licitacion.fechaPublicacion || licitacion.fecha_publicacion)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Cierre:</span>
                    <span className="font-medium">{formatDate(licitacion.fechaCierre || licitacion.fecha_cierre)}</span>
                  </div>
                  {daysUntilClose !== null && (
                    <div className="pt-2 border-t border-gray-200">
                      <div className={`text-center p-2 rounded ${
                        daysUntilClose < 0 
                          ? 'bg-red-100 text-red-800' 
                          : daysUntilClose <= 7 
                          ? 'bg-orange-100 text-orange-800' 
                          : 'bg-green-100 text-green-800'
                      }`}>
                        {daysUntilClose < 0 
                          ? `Cerrada hace ${Math.abs(daysUntilClose)} días` 
                          : daysUntilClose === 0 
                          ? 'Cierra hoy' 
                          : `${daysUntilClose} días restantes`
                        }
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Información de contacto */}
              {(licitacion.responsable || licitacion.empresa_solicitante || licitacion.organismo) && (
                <div className="bg-white border border-gray-200 rounded-lg p-4">
                  <h4 className="text-lg font-semibold text-gray-900 mb-4">Información de Contacto</h4>
                  <div className="space-y-2 text-sm">
                    {licitacion.responsable && (
                      <div>
                        <span className="text-gray-600">Responsable:</span>
                        <p className="font-medium">{licitacion.responsable}</p>
                      </div>
                    )}
                    {licitacion.empresa_solicitante && (
                      <div>
                        <span className="text-gray-600">Empresa:</span>
                        <p className="font-medium">{licitacion.empresa_solicitante}</p>
                      </div>
                    )}
                    {licitacion.organismo && (
                      <div>
                        <span className="text-gray-600">Organismo:</span>
                        <p className="font-medium">{licitacion.organismo}</p>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="flex justify-end p-6 border-t border-gray-200">
          <button
            onClick={onClose}
            className="bg-gray-600 text-white px-6 py-2 rounded-lg hover:bg-gray-700 transition-colors"
          >
            Cerrar
          </button>
        </div>
      </div>
    </div>
  );
}
